mapper.select:
SELECT a.*,b.id as 'member.id',b.name as 'member.name' FROM tbl_score_detail as a LEFT JOIN tbl_member as b ON a.member_id=b.id
AND a.member_id=#{memberId}
ORDER BY id DESC

select * from tbl_score_detail WHERE member_id=#{memberId} and score_rule_id=#{scoreRuleId}






    and to_days(create_time) = to_days(now())


    and yearweek(date_format(create_time,'%Y-%m-%d')) = yearweek(now());


    and date_format(create_time,'%Y-%m') = date_format(now(),'%Y-%m')


    and year(create_time) = year(NOW())
