mapper.select:

    select id as id,login_name as loginName,no as no,name as name,user_type as userType,salt as salt 
    from sys_user 
    where id=#{userId}


    select id as id,login_name as loginName,no as no,name as name,user_type as userType,password as password,salt as salt
    from sys_user 
    where login_name=#{loginName}


    select id as id,login_name as loginName,no as no,name as name,user_type as userType,salt as salt 
    from sys_user 
    where login_name=#{loginName}  and password=#{password}
